    "        'Chennai', 'Pune', 'Bhopal', 'Jaipur'\n",
    "    ]\n",
    "    severities = ['Low', 'Medium', 'High']\n",
    "    for i in range(num_entries):\n",
    "        timestamp = datetime.now() - timedelta(days=random.randint(30, 365*5)) \n",
    "        rainfall_amount = round(random.uniform(40, 150), 2)\n",
