    "        elif severity == 'Medium':\n",
    "            deaths = random.randint(0, 3)\n",
    "        \n",
    "        data.append({\n",
    "            'timestamp': timestamp,\n",
    "            'location': location,\n",
    "            'rainfall_amount_mm': rainfall_amount,\n",
    "            'water_level_m': water_level,\n",
    "            'severity': severity,\n",
    "            'deaths': deaths\n",
    "        })\n",
